import React, { useEffect, useState } from 'react';
import Link from 'next/link';
import Login from './Login';
import { BiAddToQueue } from 'react-icons/bi';
import { userVar } from '../providers/apollo/vars';
import { useQuery } from '@apollo/client';
import { currentUser, getUser } from '../providers/apollo/queries';
import { CgMediaLive } from 'react-icons/cg';
import LoggedIn from './LoggedIn';
import ThemeMenu from './ThemeMenu';
import Notifications from './Notifications';

export default function Nav() {
  const { data } = useQuery(getUser);
  const [theme, setTheme] = useState(null);
  const user = useQuery(currentUser).data.currentUser;
  useEffect(() => {
    if (data) userVar(data.user);
  }, [data]);

  useEffect(() => {
    document.documentElement.setAttribute(
      'data-theme',
      theme ?? localStorage?.getItem('theme')
    );
  }, [theme]);
  return (
    <>
      <div className="navbar mb-2 shadow-lg bg-neutral text-neutral-content ">
        <div className="flex-none px-2 mx-2">
          <span className="text-lg font-bold">
            <Link href="/">Debatable </Link>
          </span>
        </div>
        <div className="flex-1 px-2 mx-2">
          <div className="items-stretch hidden lg:flex">
            <span className="btn btn-ghost btn-sm rounded-btn">
              <Link href="/tags">Explore</Link>
            </span>
            <Link href="/livedebates">
              <span className="btn btn-ghost btn-sm rounded-btn">
                <CgMediaLive className="mr-1" size="24px" />
                Live
              </span>
            </Link>

            {user && (
              <span className="btn btn-ghost btn-sm rounded-btn">
                <Link href="/create">
                  <span className="flex justify-center">
                    <BiAddToQueue size="24px" className="mr-1" />
                    Debate
                  </span>
                </Link>
              </span>
            )}
          </div>
        </div>
        <div className="flex-none">
          <div className="dropdown dropdown-end">
            <div tabIndex="0">
              <button className="btn btn-square btn-ghost">
                <div className="indicator">
                  <div className="indicator-item badge badge-secondary badge-xs"></div>
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    fill="none"
                    viewBox="0 0 24 24"
                    className="inline-block w-6 h-6 stroke-current"
                  >
                    <path
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      strokeWidth="2"
                      d="M15 17h5l-1.405-1.405A2.032 2.032 0 0118 14.158V11a6.002 6.002 0 00-4-5.659V5a2 2 0 10-4 0v.341C7.67 6.165 6 8.388 6 11v3.159c0 .538-.214 1.055-.595 1.436L4 17h5m6 0v1a3 3 0 11-6 0v-1m6 0H9"
                    ></path>
                  </svg>
                </div>
              </button>
            </div>
            <Notifications user={user} />
          </div>
        </div>
        <ThemeMenu setTheme={setTheme} />
        <div className="flex-none">
          <div class="dropdown dropdown-end">
            <div className="avatar">
              <div className="rounded-full w-10 h-full m-1">
                {user ? (
                  <div tabindex="0">
                    {user.photo ? (
                      <img src={user.photo} />
                    ) : (
                      <img src="data:image/jpeg;base64," />
                    )}
                  </div>
                ) : (
                  <div tabindex="0">
                    <img src="data:image/jpeg;base64," />
                  </div>
                )}

                <ul
                  tabindex="0"
                  class="p-2 shadow menu dropdown-content bg-base-100 rounded-box w-52 "
                >
                  {user ? <LoggedIn /> : <Login />}
                </ul>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}
